import React, { Component } from 'react'
import Counter from './counter';

export default class Counters extends Component {
    state = {  
        counters: [
            {id:1, value: 4},
            {id:2, value: 0},
            {id:3, value: 0},
            {id:4, value: 0}
        ]
    }
     ///State là nơi lưu trữ data rieng của component đó mà các components khác ko access hay edit vào dc/ local data store của component đó
     handleIncrement = () => {
        this.setState({value: this.state.counters.value +1})
    }
    ///value da có sẵn nên ko cần initialize 
     handleDelete = counterId => {
        const counters = this.state.counters.filter(c => c.id !== counterId)
        this.setState({counters})
    /// the new constant will overide the properties of the array 
    ///{} r used to pass an object  
    }
    ///handleDelete này là 1 method hoac con dc goi la  event handler
    handleReset = () => {
        const counters = this.state.counters.filter(c => c.value === 0)
        this.setState({counters})
    } 
    render() { 
        return (
            <div>
            <button onClick={this.handleReset} className="btn btn-primary btn-sm m-2">Reset</button>
            {this.state.counters.map(counter => 
            <Counter 
            key={counter.id} 
            // value={counter.value} 
            // id={counter.id}
            onDelete={this.handleDelete}
///The counter object carries all the data of the object so owe can omit its props like value, if, onDelete 
            counter={counter}
            />)} 
            </div>
         );
    }

     ///<Counter key={counter.id} value={counter.value} /> giải thích: counter.value ở đây là access vào value của counters và lấy cái value attribute từ counters (array) của state (object) pass vào cái counter component.
     ///Which biến nó thành 1 prop của counter => nếu muốn access nó ở bên counter.jsx thì phải ghi là this.props.value còn access đến nó trong trang chính chủ là counters (vì nó là local data của counters state) thì phải ghi là this.state.counters[số] vì là array
     ///vi du: key={counter.id} thì key là 1 props còn {counter.id} là value giống như hồi học Python k:v
     ///value={counter.value} sau đó ta dăt tên cho nó là value, value này dc gọi là attribute của this.props của counter khi console.log co thể thấy hoặc dc goi la props của counter.
     ///onDelete={this.handleDelete} onDelet này cunng la 1 prop act as a function
}
