import React, { Component } from 'react'
export default class Counters extends Component {
    state = { 
        count:0,
        tags: ['tag1','tag2','tag3']
     }

    render() { 

        return (
        <React.Fragment>
            <span>{this.state.count}</span>
            <span className={this.changeBadgeClasses()}>
                {this.formatCount()}
                </span>
                <div>
                    {this.state.count === 0 && "Enter a value"}
                    {this.displayWarning}
                </div>
            <button onClick = {handleIncrement} className="btn btn-secondary btn-sm">Increment</button>
            <ul>{this.state.tags.map(tag =><li key={tag.id}>{tag}</li> )}</ul>
        </React.Fragment>
        ); 
    }
    handleIncrement = () => {
        this.state.count++
        this.setState
    }
    displayWarning() {
        if (this.state.count === 0) return 'Please enter a value'
        return 'Complete'
    }
    changeBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === 0) ? "warning" : "primary";
    }

    formatCount() {
        return this.state.count === 0 ? 'Zero' : this.state.count
    }
}
