import React, { Component } from 'react'
export default class Counters extends Component {
    state = { 
        value:this.props.value
     }
     handleIncrement = (product) => {
        console.log(product)
        this.setState({value: this.state.value +1})
    }
    handleIncrement = (product) => {
        console.log(product)
        this.setState({value: this.state.value -1})
    }
    render() { 
        console.log("props", this.props)
        /// chỉ cần dc initialize là 1 prop thì muốn call nó thì phải thêm từ props vào trc ex: this.props.A 
        return (
        <React.Fragment>
                <div>
            <span className={this.changeBadgeClasses()}>
                {this.formatCount()}
                </span>
                </div>
                <div>
                    {this.displayWarning}
                </div>
            <button onClick = {() => this.handleIncrement()} className="btn btn-secondary btn-sm">Increment</button>
            <button onClick = {() => this.handleDelete()} className="btn btn-secondary btn-sm">Delete</button>
        </React.Fragment>
        ); 
    }

    displayWarning() {
        if (this.state.count === 0) return 'Please enter a value'
        return 'Complete'
    }
    changeBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === 0) ? "warning" : "primary";
    }

    formatCount() {
        const {value} = this.state
        return value === 0 ? 'Zero' : value
    }
}
