import React, { Component } from 'react'
export default class Counters extends Component {
    state = { 
        value:this.props.value


     }
     handleIncrement = (product) => {
        console.log(product)
        this.setState({value: this.state.value +1})
    }
    render() { 
        console.log("props", this.props)
        ///props ở đây là một object chứa tất cả các attributes mà attribute ở đây là props (data từ state của components input vào đây khác vd: Counters) dc pass vào đây 
        return (
        <React.Fragment>
                <div>
            <span className={this.changeBadgeClasses()}>
                {this.formatCount()}
                </span>
                </div>
                <div>
                    {this.displayWarning}
                </div>
            <button onClick = {() => this.handleIncrement()} className="btn btn-secondary btn-sm">Increment</button>
            <button onClick = {this.props.handleDelete} className="btn btn-secondary btn-sm">Delete</button>
        </React.Fragment>
        ); 
    }
    ///hai cái button này ko giống nhau và ko thể reuse code của handleIncrement cho handleDelete dù chỉ là tăng 1 giảm 1 giống nhau 
    /// Cái trên dc pass vào như là 1 function dc gán qua 1 instance là handleIncrement hay ta gọi nó là 1 Method, chỉ hoạt động trong counter component only vì nó dc tạo ra ở trong component đó lun nên nó có thể edit dc state của component đó 
    ///Còn cái handleDelete có mục đích dc tạo ra để delete cái data dc pass vào Counter component hay nói các khác là để delete props của counter component. Nó dc render trong counter nên ko thể access hay edit dc state của component khác (Counters) ngoài component mà nó đang ở trong (Counter) nên ở đây ta dùng pphap raising handler - calling handler
    /// Trong truong hop này, giai thich ra là we create a method in the counter components (handleDelete) and pass it via a props (onDelete) TO the Counter Component
    ///handleDelete dc pass vào giống như 1 props (this.props.handleDelete)giong như ví dụ value ở trên vậy (this.props.value)
    displayWarning() {
        if (this.state.count === 0) return 'Please enter a value'
        return 'Complete'
    }
    changeBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === 0) ? "warning" : "primary";
    }

    formatCount() {
        const {value} = this.state
        return value === 0 ? 'Zero' : value
    }
}
