import React, { Component } from 'react'
import Counter from './counter';

class Counters extends Component {
    state = {  
        counters: [
            {id:1, value: 4},
            {id:2, value: 0},
            {id:3, value: 0},
            {id:4, value: 0}
        ]
    }
     ///State là nơi lưu trữ data rieng của component đó mà các components khác ko access hay edit vào dc/ local data store của component đó
    render() { 
        return ( 
            <div>
            {this.state.counters.map(counter => <Counter key={counter.id} value={counter.value} />)} 
            </div>
         );
    }
}
     ///
     ///
export default Counters;
